// src/pages/OrganizerDashboard.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';

function OrganizerDashboard() {
  const [events, setEvents] = useState([]);
  const [formData, setFormData] = useState({
    eventName: '', date: '', time: '', venue: ''
  });

  const token = localStorage.getItem('token');

  // Fetch events
  useEffect(() => {
    axios.get('http://localhost:5000/api/events/my', {
      headers: { Authorization: `Bearer ${token}` }
    })
    .then(res => setEvents(res.data.events))
    .catch(err => console.error(err));
  }, [token]);

  // Handle input changes
  const handleChange = (e) => {
    setFormData({...formData, [e.target.name]: e.target.value});
  };

  // Submit new event
  const handleSubmit = (e) => {
    e.preventDefault();
    axios.post('http://localhost:5000/api/events/create', formData, {
      headers: { Authorization: `Bearer ${token}` }
    })
    .then(res => {
      alert(res.data.message);
      window.location.reload(); // refresh event list
    })
    .catch(err => alert('Event creation failed'));
  };

  return (
    <div className="container mt-4">
      <h2>Create New Event</h2>
      <form onSubmit={handleSubmit}>
        <input className="form-control mb-2" name="eventName" placeholder="Event Name" onChange={handleChange} required />
        <input className="form-control mb-2" type="date" name="date" onChange={handleChange} required />
        <input className="form-control mb-2" type="time" name="time" onChange={handleChange} required />
        <input className="form-control mb-2" name="venue" placeholder="Venue" onChange={handleChange} required />
        <button className="btn btn-primary" type="submit">Create Event</button>
      </form>

      <hr />
      <h3>Your Events</h3>
      <ul className="list-group">
        {events.map((event, idx) => (
          <li key={idx} className="list-group-item">
            <strong>{event.eventName}</strong><br />
            {event.date} at {event.time} | {event.venue}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default OrganizerDashboard;
